import { ObjectId } from 'mongodb';
import dBClient from './db';

const FOLDER = 'folder';
const FILE = 'file';
const IMAGE = 'image';
const VALID_FILE_TYPES = [FOLDER, FILE, IMAGE];
const FOLDER_PATH = process.env.FOLDER_PATH || '/tmp/files_manager';

class FilesCollection {
  constructor() {
    this.files = dBClient.filesCollection();
  }

  async findById(id) {
    return this.files.findOne({ _id: ObjectId(id) });
  }
}

export default class File {
  constructor(name, type, parentId, isPublic, data) {
    this.name = name;
    this.type = type;
    this.parentId = parentId;
    this.isPublic = isPublic;
    this.data = data;
    this.filesCollection = new FilesCollection();
  }

  validate() {
    if (!this.name) {
      return 'Missing name';
    }

    if (!this.type || !VALID_FILE_TYPES.includes(this.type)) {
      return 'Missing type';
    }

    if (!this.data && this.type !== FOLDER) {
      return 'Missing data';
    }

    if (this.parentId) {
      const parent = this.filesCollection.findById(this.parentId);
      if (!parent) {
        return 'Parent not found';
      }

      if (parent.type !== FOLDER) {
        return 'Parent is not a folder';
      }
    }

    return null;
  }
}

